mapper.select:
		select * from edu_comment
		
			
				and content like '%${content}%'
			
			
				and type = #{type}
			
			
				and user_id in(SELECT user_id FROM edu_user WHERE email LIKE
				'%${email}%')
			
			
				and addtime > #{start}
			
			
				and addtime < #{end}

mapper.update:
update edu_comment set
PRAISE_COUNT=PRAISE_COUNT+1 where
comment_id=#{comment_id}
